
public class charAtExample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String st="Welcome to FACE potal";
		int stlen=st.length();
		System.out.println("Char at 1st index is: "+st.charAt(1));//Fetching first character.
		System.out.print("Character at last index is: "+st.charAt(stlen-1));//The character is present at the length-1 index.
		

	}

}
